mapper.select:

select 

from jk.t_jk_bank_plant_port where 1=1 

  		AND id = #{id,jdbcType=VARCHAR}


  		AND bank_code = #{bankCode,jdbcType=VARCHAR}


  		AND plant_Code = #{plantCode,jdbcType=VARCHAR}


  		AND is_concentrate = #{isConcentrate,jdbcType=VARCHAR}

 
  		AND batch_flag = #{batchFlag,jdbcType=VARCHAR}

select a.bank_code, array_to_string(ARRAY(SELECT unnest(array_agg(a.plant_code))),',') AS plant_code, array_to_string(ARRAY(SELECT unnest(array_agg(a.batch_flag))),',') AS batch_flag from jk.t_jk_bank_plant_port a where 1=1
AND a.bank_code = #{bankCode}
AND a.is_Concentrate = #{isConcentrate}
group by bank_code

select a.id, b.label as batch_flag, c.label as bank_name, d.label as plant_name, e.label as is_Concentrate from jk.t_jk_bank_plant_port a left join (select value,label from t_gl_dict where type ='com_deduct_type') b on a.batch_flag = b.value left join (select value,label from t_gl_dict where type ='jk_open_bank') c on a.bank_code = c.value left join (select value,label from t_gl_dict where type ='jk_deduct_plat') d on a.plant_code = d.value left join (select value,label from t_gl_dict where type ='yes_no') e on a.is_Concentrate = e.value where 1=1
AND a.bank_code = #{bankCode}
AND a.is_Concentrate = #{isConcentrate}
order by a.bank_code,plant_code desc

select 

from jk.t_jk_bank_plant_port where 1=1 

  		AND id != #{id,jdbcType=VARCHAR}


  		AND bank_code = #{bankCode,jdbcType=VARCHAR}


  		AND plant_Code = #{plantCode,jdbcType=VARCHAR}


  		AND is_concentrate = #{isConcentrate,jdbcType=VARCHAR}

 
  		AND batch_flag = #{batchFlag,jdbcType=VARCHAR}
