mapper.select:
select ROW_NUMBER() OVER() SEQUENCENUMBER, e.id, d.apply_id, a.customer_name, (CASE when w.cobo_name is not null then w.cobo_name else '' end ) as cobo_name, a.customer_cert_num as customerCertNum, manager.name as loanTeamManagerName,-- 团队经理名称 customer.name as loanManagerName,-- 客户经理名称 b.loan_team_managercode, -- 团队经理编号 b.loan_managercode, -- 客户经理 t.product_name as productType, -- 产品类型 round(d.audit_amount,2) audit_amount, round(f.fee_petition,2) fee_petition, M8.LABEL as customer_telesales_flag, b.dict_is_additional , -- 是否追加借 d.contract_code, d.contract_amount, e.grant_amount, round(sum(e.grant_amount) OVER (),2) as totalGrantMoney,--放款总额 fee_urged_service as urgeMoney, -- 催收服务费 (s.urge_moeny-s.urge_decute_moeny-s.audit_amount) as urgeDecuteMoeny, s.AUDIT_AMOUNT as grantAuditAmount, -- 已查账金额 fee_credit,--征信费 case when d.product_type = 'A021' then f.fee_urged_service+fee_credit+fee_petition else f.fee_urged_service end as feeSum, --综合费用 d.contract_months, g.middle_name, g.mid_bank_name, g.bank_card_no, lb.bank_account_name, e.dict_loan_way, h.name as storesCode, -- 门店名称 b.loan_store_orgid as storeOrgId, -- 门店id e.lending_time, -- 放款时间 lending.name as lending_user_id, -- 放款人员,导出时需要进行转换id checks.name as check_emp_id, -- 审核专员 ,导出时需要进行转换id M7.LABEL as loan_flag, -- 标识 M6.LABEL as loan_urgent_flag, -- 加急标识 b.trust_cash, e.loan_code, e.submissiondate, e.grant_pch, -- 提交批次 e.grant_batch -- 放款批次 from jk.t_jk_loan_grant e LEFT JOIN jk.t_jk_contract d ON d.contract_code=e.contract_code LEFT JOIN jk.t_jk_loan_customer a ON a.loan_code=e.loan_code LEFT JOIN jk.t_jk_loan_info b ON b.loan_code=e.loan_code LEFT JOIN (select loan_code,STRING_AGG(cobo_name,';') as cobo_name from jk.t_jk_loan_coborrower group by loan_code) w on w.loan_code = e.loan_code LEFT JOIN jk.t_jk_contract_fee f ON f.contract_code=d.contract_code LEFT JOIN jk.t_jk_loan_bank lb on lb.loan_code = b.loan_code and lb.bank_top_flag = '1' and lb.repayment_flag = '0' LEFT JOIN jk.t_jk_urge_services_amount s ON s.r_grant_id=e.id and s.return_logo ='0' LEFT JOIN jk.t_jk_middle_person g ON g.id=e.mid_id LEFT JOIN jk.t_gl_org h ON h.ID = b.loan_store_orgid LEFT JOIN jk.t_gl_jk_product t ON t.product_code=d.product_type LEFT JOIN jk.t_gl_user manager ON manager.id=b.loan_team_managercode -- 团队经理 LEFT JOIN jk.t_gl_user customer ON customer.id=b.loan_managercode -- 客户经理 LEFT JOIN jk.t_gl_user lending ON lending.id=e.lending_user_id LEFT JOIN jk.t_gl_user checks ON checks.id=e.check_emp_id
LEFT JOIN (select value,LABEL from JK.T_GL_DICT where TYPE = 'jk_urgent_flag') M6 ON M6.VALUE = b.loan_urgent_flag LEFT JOIN (select value,LABEL from JK.T_GL_DICT where TYPE = 'jk_channel_flag') M7 ON M7.VALUE = b.loan_flag LEFT JOIN (select value,LABEL from JK.T_GL_DICT where TYPE = 'jk_telemarketing') M8 ON M8.VALUE = a.customer_telesales_flag where b.dict_loan_status in( '70','87','88','89','90','91','92' )
and b.model = #{loanGrant.model}
and (b.loan_flag is null or b.loan_flag in ('1','2','4')) and (b.model = '0' or b.model is null)
and a.customer_name LIKE CONCAT('%', #{loanGrant.customerName}, '%')
and b.trust_cash= #{loanGrant.trustCash}
and d.class_type = #{loanGrant.classType}
and d.product_type = #{loanGrant.productName}
and e.dict_loan_way =#{loanGrant.dictLoanWay}
and g.mid_bank_name LIKE CONCAT('%',#{loanGrant.midBankName},'%')
and b.loan_flag = #{loanGrant.loanFlag}
and to_date(to_char(e.lending_time,'yyyy-mm-dd')) >= #{loanGrant.startDate}
and to_date(to_char(e.lending_time,'yyyy-mm-dd')) <= #{loanGrant.endDate}
and to_date(to_char(e.submissiondate,'yyyy-mm-dd')) >= #{loanGrant.subStartDate}
and to_date(to_char(e.submissiondate,'yyyy-mm-dd')) <= #{loanGrant.subEndDate}
and e.contract_code = #{loanGrant.contractCode}
and e.lending_user_id = #{loanGrant.lendingUserId}
and e.grant_batch = #{loanGrant.grantPch,jdbcType=VARCHAR}
and a.customer_cert_num like '%'||#{loanGrant.customerCertNum}||'%'
and e.grant_pch = #{loanGrant.submissionBatch}
and b.loan_store_orgid in
#{item}
and e.contract_code in
#{item}
order by lending_time DESC

select a.apply_id
from jk.t_jk_contract a
where a.contract_code=#{contractCode}

select contract_code from  t_jk_loan_info a 
LEFT JOIN t_jk_contract b ON b.loan_code = a.loan_code 
where trim(a.frozen_code) != '' and b.contract_code = #{contractCode}

select d.apply_id as batId, a.contract_code as businessId, a.grant_amount as amount, b.apply_bank_name as bankName, b.bank_card_no as accountNo, b.bank_account_name as accountName, y.area_name as bankCity, t.area_name as bankProv, b.card_bank as bankId, c.dict_cert_type as idType, c.customer_cert_num as idNo, c.customer_phone_first as mobile
from jk.t_jk_loan_grant a left join jk.t_cj_customer_bank_info b on b.loan_code = a.loan_code LEFT JOIN jk.t_gl_province_city t ON t.area_code = b.bank_province LEFT JOIN jk.t_gl_province_city y ON y.area_code = b.bank_city LEFT JOIN jk.t_jk_loan_customer c ON c.loan_code = a.loan_code LEFT JOIN jk.t_cj_loan_info d ON d.loan_code = a.loan_code
where a.contract_code=#{contractCode}
and d.dict_loan_status = #{dictLoanStatus}
and a.dict_loan_way = #{dictLoanWay}

