mapper.select:
select e.apply_id as batId, a.contract_code as businessId, (CASE when a.grant_fail_amount = 0 then a.grant_amount else a.grant_fail_amount end ) as amount, d.bank_name as bankId, province.area_name as bankProv, city.area_name as bankCity, d.bank_branch as bankName, d.bank_account as accountNo, d.bank_account_name as accountName, c.dict_cert_type as idType, c.customer_cert_num as idNo, c.customer_phone_first as mobile, '00' as accountType from t_jk_loan_grant a left join t_jk_loan_info b on b.loan_code = a.loan_code left join t_jk_contract e on e.loan_code = b.loan_code left join t_jk_loan_customer c on c.loan_code = a.loan_code left join t_jk_loan_bank d on a.loan_code = d.loan_code and d.bank_top_flag='1' and d.repayment_flag = '0' left join jk.t_gl_province_city_cmb province on bank_province = province.id left join jk.t_gl_province_city_cmb city on bank_city = city.id where a.dict_loan_way!='0'
and e.contract_code in( ${contractCode} )
and b.dict_loan_status != #{dictLoanStatus}
and a.dict_loan_way = #{dictLoanWay}

select grant_pch from( select grant_pch,loan_code from (
select grant_pch,loan_code from t_jk_loan_grant where grant_pch like
'%'||#{letter,jdbcType=VARCHAR}||'%'  and to_char(create_time,'yyyy-MM-dd') = #{queryTime}
 order by grant_pch desc limit 1
)
UNION
select grant_pch,loan_code from t_jk_loan_grant where grant_pch like
'%'||#{urgentFlag,jdbcType=VARCHAR}||'%'  and to_char(create_time,'yyyy-MM-dd') like #{queryTime}
 order by grant_pch desc limit 1) a left join jk.t_jk_loan_info info on a.loan_code = info.loan_code and info.DICT_LOAN_STATUS = #{loanStatus}

select b.apply_id applyId,b.loan_code loanCode,e.id, b.loaninfo_oldornew_flag loanInfoOldOrNewFlag, d.loan_name customerName, d.loan_cert_num as customerCertNum, d.cobo_name coBorrowing, round(sum(e.grant_amount) OVER (),2) as totalGrantMoney, b.loan_team_managercode as loanTeamManagerCode, b.loan_managercode as loanManagerCode, b.loan_customer_service as loanCustomerServiceName, b.loan_survey_emp_id, '信借' as classType, t.product_name as productType, round(d.audit_amount,2) auditAmount, round(f.fee_petition,2) feePetition, b.loan_urgent_flag urgentFlag, d.contract_code contractCode , round(d.contract_amount,2) contractAmount, round(e.grant_amount,2) grantAmount , round(s.urge_moeny,2) urgeMoney, round(s.urge_moeny - (COALESCE(s.urge_decute_moeny, 0) + COALESCE(s.audit_amount, 0)),2)urgeDecuteMoeny, d.contract_months contractMonths, mp.middle_name middleName, --开户姓名 mp.mid_bank_name midBankName, --开户行 mp.bank_card_no bankCardNo, --账户 h.name as storesCode, TO_CHAR(E.LENDING_TIME,'yyyy-MM-dd') lendingTime, e.lending_user_id loanOfficerName, e.check_emp_id checkEmpName, b.loan_flag loanFlag, e.loan_code, e.grant_pch grantPch, e.grant_batch submissionBatch, e.submissiondate submissionDate,
M6.LABEL,M7.LABEL CHANNEL_NAME,CHECKS.NAME CHECKNAME,SURVEY_EMP.NAME SURVEYNAME,SERVICE.NAME SERVICENAME, TEAM.NAME TEAMNAME,MANAGER.NAME MANAGERNAME, (select cobo_name from jk.t_jk_loan_coborrower w where id=(select best_coborrower_id from( select best_coborrower_id from jk.t_jk_audit_result t where loan_code=e.loan_code order by create_time desc limit 1))) as sercurityNames
FROM JK.T_JK_LOAN_GRANT e LEFT JOIN jk.t_jk_loan_info b ON e.loan_code = b.loan_code LEFT JOIN jk.t_jk_contract d ON e.contract_code = d.contract_code LEFT JOIN JK.T_GL_USER TEAM ON B.LOAN_TEAM_MANAGERCODE = TEAM.ID LEFT JOIN JK.T_GL_USER MANAGER ON B.LOAN_MANAGERCODE = MANAGER.ID LEFT JOIN JK.T_GL_USER SERVICE ON B.LOAN_CUSTOMER_SERVICE = SERVICE.ID LEFT JOIN JK.T_GL_USER SURVEY_EMP ON B.LOAN_SURVEY_EMP_ID = SURVEY_EMP.ID LEFT JOIN JK.T_GL_USER CHECKS ON E.CHECK_EMP_ID = CHECKS.ID
LEFT JOIN jk.t_jk_contract_fee f ON d.contract_code = f.contract_code LEFT JOIN jk.t_jk_middle_person mp ON mp.id=e.mid_id LEFT JOIN jk.t_jk_urge_services_amount s ON e.id = s.r_grant_id and s.return_logo ='0' LEFT JOIN jk.t_jk_loan_bank g ON b.loan_code = g.loan_code and g.bank_top_flag=1 and g.repayment_flag='1' LEFT JOIN JK.T_GL_DICT M8 ON g.bank_name = M8.VALUE AND M8.TYPE = 'jk_open_bank' LEFT JOIN jk.t_gl_org h ON b.loan_store_orgid = h.ID LEFT JOIN jk.t_gl_jk_product t ON d.product_type = t.product_code LEFT JOIN jk.t_gl_jk_product_type p ON b.class_type = p.class_type LEFT JOIN JK.T_GL_DICT M6 ON B.LOAN_URGENT_FLAG = M6.VALUE AND M6.TYPE = 'jk_urgent_flag' LEFT JOIN JK.T_GL_DICT M7 ON B.LOAN_FLAG = M7.VALUE AND M7.TYPE = 'jk_channel_flag' WHERE 1=1 AND b.LOAN_FLAG = #{param.loanFlag} AND e.lending_time IS NOT NULL and b.dict_loan_status in('70','87','88','89','90','91','92') and d.product_type != 'A020'
and d.loan_name LIKE '%${param.customerName}%'
and d.class_type = #{param.classType}
and e.contract_code = #{param.contractCode}
and e.grant_pch = #{param.grantPch,jdbcType=VARCHAR}
and e.lending_time >= to_timestamp(#{param.lendingTimeStrat}||' 00:00:00','YYYY-MM-DD HH24:MI:SS')
and e.lending_time <= to_timestamp(#{param.lendingTimeEnd}||' 23:59:59','YYYY-MM-DD HH24:MI:SS')
and d.loan_cert_num LIKE '%${param.customerCertNum}%'
and e.grant_pch LIKE '%${param.grantPch}%'
and e.grant_batch LIKE '%${param.submissionBatch}%'
and to_date(to_char(e.submissiondate,'yyyy-mm-dd')) = #{param.submissionDate}
and b.loan_urgent_flag = #{param.urgentFlag}
and b.loan_store_orgid IN
#{storeCode}
and b.loan_code IN
#{item}
order by e.lending_time desc

select ROW_NUMBER() OVER() SEQUENCENUMBER, e.id, d.apply_id, a.customer_name, a.customer_cert_num as customerCertNum, t.product_name as productType, d.contract_code, d.contract_amount, g.middle_name, (select d.label from jk.t_gl_dict d where d.value=lb.bank_name and d.type='jk_open_bank') AS bank_name, lb.bank_account AS bank_card_no, lb.bank_account_name, e.dict_loan_way, h.name as storesCode, b.loan_store_orgid as storeOrgId, e.lending_time, M7.LABEL as loan_flag, e.loan_code, d.contract_fact_day, b.dict_loan_status, (select label from jk.t_gl_dict where type='jk_loan_model' and value=b.model) AS model from jk.t_jk_loan_grant e LEFT JOIN jk.t_jk_contract d ON d.contract_code=e.contract_code LEFT JOIN jk.t_jk_loan_customer a ON a.loan_code=e.loan_code LEFT JOIN jk.t_jk_loan_info b ON b.loan_code=e.loan_code LEFT JOIN jk.t_jk_loan_bank lb on lb.loan_code = b.loan_code and lb.bank_top_flag = '1' and lb.repayment_flag = '1' LEFT JOIN jk.t_jk_middle_person g ON g.id=e.mid_id LEFT JOIN jk.t_gl_org h ON h.ID = b.loan_store_orgid LEFT JOIN jk.t_gl_jk_product t ON t.product_code=d.product_type LEFT JOIN (select value,LABEL from JK.T_GL_DICT where TYPE = 'jk_channel_flag') M7 ON M7.VALUE = b.loan_flag where b.dict_loan_status in( '70','87','88','89','90','91','92' )
and b.model = #{model}
and a.customer_name LIKE CONCAT('%', #{customerName}, '%')
and lb.bank_name in
#{item}
and b.loan_flag = #{loanFlag}
and (b.loan_flag is null or b.loan_flag in ('0','1','2','4'))
and lb.bank_account = #{bankCardNo}
and d.product_type = #{productCode}
and to_date(to_char(e.lending_time,'yyyy-mm-dd')) >= #{startDate}
and to_date(to_char(e.lending_time,'yyyy-mm-dd')) <= #{endDate}
and to_date(to_char(d.contract_fact_day,'yyyy-mm-dd')) >= #{htStartDate}
