mapper.update:
update jk.t_jk_credit_report_detailed
credit = #{credit}, CREDIT_SOURCE = #{creditSource}, CREDIT_CODE = #{creditCode}, QUERY_TIME = #{queryTime}, REPORT_TIME = #{reportTime}, name = #{name}, sex = #{sex}, CERT_TYPE = #{certType}, CERT_NO = #{certNo}, birthday = #{birthday}, HIGHEST_DEGREE = #{highestDegree}, HIGHEST_EDUCATION = #{highestEducation}, ZIP_CODE = #{zipCode}, HOME_PHONE = #{homePhone}, UNIT_PHONE = #{unitPhone}, MOBILE_PHONE = #{mobilePhone}, EMAIL = #{email}, MARRY_STATUS = #{marryStatus}, MATE_NAME = #{mateName}, MATE_CERT_TYPE = #{mateCertType}, MATE_CERT_NO = #{mateCertNo}, MATE_COMPANY = #{mateCompany}, MATE_PHONE = #{matePhone}, CONTACT_ADD_PROVINCE = #{contactAddProvince}, CONTACT_ADD_CITY = #{contactAddCity}, CONTACT_ADD_AREA = #{contactAddArea}, CONTACT_ADDRESS = #{contactAddress}, NATIVE_ADD_PROVINCE = #{nativeAddProvince}, NATIVE_ADD_CITY = #{nativeAddCity}, NATIVE_ADD_AREA = #{nativeAddArea}, NATIVE_ADDRESS = #{nativeAddress}, modify_by = #{modifyBy}, modify_time = #{modifyTime}, del_flag = '0'
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select
	*
from
	jk.t_jk_credit_report_detailed
where 
	1=1

		and loan_Code = #{loanCode,jdbcType=VARCHAR}


		and dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}


		and r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}

select c.id, c.mobilePhone, c.homePhone, c.marryStatus, c.matePhone, c.unitPhone, c.livePprovince, c.liveCity, c.liveArea, c.liveAddress, c.queryTime, d.getinfo_time, d.unit_name as unitName, pr.AREA_NAME as unitProvince, ci.AREA_NAME as unitCity, ar.AREA_NAME as unitArea, d.unit_address as unitAddress from (select a.id, a.query_time as queryTime, a.mobile_phone as mobilePhone, a.home_phone as homePhone, dic.label as marryStatus, a.mate_phone as matePhone, a.unit_phone as unitPhone, pro.AREA_NAME as livePprovince, cit.AREA_NAME as liveCity, are.AREA_NAME as liveArea, b.live_address as liveAddress from jk.t_jk_credit_report_detailed a left join jk.t_jk_credit_live_info b on a.id = b.relation_id left join jk.t_gl_dict dic on a.marry_status = dic.value and dic.type='jk_marriage' left join jk.T_GL_PROVINCE_CITY pro on b.live_province = pro.AREA_CODE left join jk.T_GL_PROVINCE_CITY cit on b.live_city = cit.AREA_CODE left join jk.T_GL_PROVINCE_CITY are on b.live_area = are.AREA_CODE where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}
and a.r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
order by b.getinfo_time desc limit 1) c left join jk.t_jk_credit_occupation_info d on c.id = d.relation_id left join jk.T_GL_PROVINCE_CITY pr on d.unit_province = pr.AREA_CODE left join jk.T_GL_PROVINCE_CITY ci on d.unit_city = ci.AREA_CODE left join jk.T_GL_PROVINCE_CITY ar on d.unit_area = ar.AREA_CODE order by d.getinfo_time desc limit 1

select sum(b.CERDIT_LINE) as CerditLine, sum(b.USED_AMOUNT) as UsedAmount, f.shouldRepayAmount, f.currentOverdueTotal from jk.t_jk_credit_report_detailed a left join jk.t_jk_credit_card_detailed_one b on a.id = b.relation_id left join (select a.id, sum(c.should_repay_amount) as shouldRepayAmount , sum(c.current_overdue_total) as currentOverdueTotal from jk.t_jk_credit_report_detailed a left join jk.t_jk_credit_card_detailed_one b on a.id = b.relation_id left join jk.t_jk_credit_card_detailed_two c on b.id = c.relation_id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}
and a.r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.CARD_TYPE = #{cardType,jdbcType=VARCHAR}
and b.CURRENCY = #{currency,jdbcType=VARCHAR}
group by a.id) f on a.id = f.id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}
and a.r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.CARD_TYPE = #{cardType,jdbcType=VARCHAR}
and b.CURRENCY = #{currency,jdbcType=VARCHAR}
group by f.shouldRepayAmount,f.currentOverdueTotal

select sum(b.CONTEACT_AMOUNT) as conteactAmount, sum(b.LOAN_BALANCE) as loanBalance, f.shouldRepayAmount from jk.t_jk_credit_report_detailed a left join jk.t_jk_credit_loan_detailed_one b on a.id = b.relation_id left join (select a.id, sum(c.SHOULD_REPAY_AMOUNT) as shouldRepayAmount from jk.t_jk_credit_report_detailed a left join jk.t_jk_credit_loan_detailed_one b on a.id = b.relation_id left join jk.t_jk_credit_loan_detailed_two c on b.id = c.relation_id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}
and a.r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.LOAN_TYPE in
#{item}
and b.GUARANTEE_TYPE = #{guaranteeType,jdbcType=VARCHAR}
group by a.id ) f on a.id = f.id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}
and a.r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.LOAN_TYPE in
#{item}
and b.GUARANTEE_TYPE = #{guaranteeType,jdbcType=VARCHAR}
group by f.shouldRepayAmount

select (case when report.certNo<>loaninfo.customerCertNum and CHAR_LENGTH(report.certNo)>0 and CHAR_LENGTH(loaninfo.customerCertNum)>0 then '1' else '0' end)::varchar as certNo, (case when report.mobilePhone<>loaninfo.customerPhoneFirst and CHAR_LENGTH(report.mobilePhone)>0 and CHAR_LENGTH(loaninfo.customerPhoneFirst)>0 and report.mobilePhone<> loaninfo.customerPhoneSecond and CHAR_LENGTH(loaninfo.customerPhoneSecond)>0 then '1' else '0' end)::varchar as mobilePhone, (case when report.unitName<>loaninfo.compName and CHAR_LENGTH(report.unitName)>0 and CHAR_LENGTH(loaninfo.compName)>0 then '1' else '0' end)::varchar as unitName, (case when report.homePhone<>loaninfo.customerTel and CHAR_LENGTH(report.homePhone)>0 and CHAR_LENGTH(loaninfo.customerTel)>0 then '1' else '0' end)::varchar as homePhone, (case when report.unitPhone<>loaninfo.compTel and CHAR_LENGTH(report.unitPhone)>0 and CHAR_LENGTH(loaninfo.compTel)>0 then '1' else '0' end)::varchar as unitPhone, (case when report.nuitAddress<>loaninfo.compAddress and CHAR_LENGTH(report.nuitAddress)>0 and CHAR_LENGTH(loaninfo.compAddress)>0 then '1' else '0' end)::varchar as nuitAddress, (case when report.liveAddress<>loaninfo.customerAddress and CHAR_LENGTH(report.liveAddress)>0 and CHAR_LENGTH(loaninfo.customerAddress)>0 then '1' else '0' end)::varchar as liveAddress, (case when report.marryStatus<>loaninfo.dictMartyStatus and CHAR_LENGTH(report.marryStatus)>0 and CHAR_LENGTH(loaninfo.dictMartyStatus)>0 then '1' else '0' end)::varchar as marryStatus, (case when report.mateCompany<>loaninfo.pCompName and CHAR_LENGTH(report.mateCompany)>0 and CHAR_LENGTH(loaninfo.pCompName)>0 then '1' else '0' end)::varchar as mateCompany, (case when report.matePhone<>loaninfo.mateTel and CHAR_LENGTH(report.matePhone)>0 and CHAR_LENGTH(loaninfo.mateTel)>0 then '1' else '0' end)::varchar as matePhone, (case when report.mateCertNo<>loaninfo.mateCertNum and CHAR_LENGTH(report.mateCertNo)>0 and CHAR_LENGTH(loaninfo.mateCertNum)>0 then '1' else '0' end)::varchar as mateCertNo, (case when report.mateName<>loaninfo.mateCertNum and CHAR_LENGTH(report.mateName)>0 and CHAR_LENGTH(loaninfo.mateCertNum)>0 then '1' else '0' end)::varchar as mateName from (select a.loan_code, a.CERT_NO as certNo, a.MOBILE_PHONE as mobilePhone, b.unitName as unitName, a.HOME_PHONE as homePhone, a.UNIT_PHONE as unitPhone, b.nuitAddress as nuitAddress, c.liveAddress as liveAddress, a.MARRY_STATUS as marryStatus, a.MATE_COMPANY as mateCompany, a.MATE_PHONE as matePhone, a.MATE_CERT_NO as mateCertNo, a.MATE_NAME as mateName, a.DICT_CUSTOMER_TYPE as dictCustomerType, a.R_CUSTOMER_COBORROWER_ID as rCustomerCoborrowerId from jk.t_jk_credit_report_detailed a left join ( select det.id, occ.UNIT_NAME as unitName, concat(occ.UNIT_PROVINCE,occ.UNIT_CITY,occ.UNIT_AREA,occ.UNIT_ADDRESS) as nuitAddress from jk.t_jk_credit_report_detailed det left join jk.T_JK_CREDIT_OCCUPATION_INFO occ on det.id = occ.RELATION_ID where 1=1
and det.loan_Code = #{loanCode,jdbcType=VARCHAR}
and det.id = #{id,jdbcType=VARCHAR}
order by occ.GETINFO_TIME desc limit 1 ) b on a.id=b.id left join ( select det.id, concat(liv.LIVE_PROVINCE,liv.LIVE_CITY,liv.LIVE_AREA,liv.LIVE_ADDRESS) as liveAddress from jk.t_jk_credit_report_detailed det left join jk.T_JK_CREDIT_LIVE_INFO liv on det.id = liv.RELATION_ID where 1=1
and det.loan_Code = #{loanCode,jdbcType=VARCHAR}
and det.id = #{id,jdbcType=VARCHAR}
order by liv.GETINFO_TIME desc limit 1 )c on a.id=c.id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.id = #{id,jdbcType=VARCHAR}
) report left join (select a.loan_code, a.CUSTOMER_CERT_NUM as customerCertNum, a.CUSTOMER_PHONE_FIRST as customerPhoneFirst, a.CUSTOMER_PHONE_SECOND as customerPhoneSecond, b.COMP_NAME as compName, a.CUSTOMER_TEL as customerTel, b.COMP_TEL as compTel, concat( b.COMP_PROVINCE,b.COMP_CITY,b.COMP_ARER,b.COMP_ADDRESS ) as compAddress, concat( a.CUSTOMER_LIVE_PROVINCE,a.CUSTOMER_LIVE_CITY,a.CUSTOMER_LIVE_AREA,a.CUSTOMER_ADDRESS ) as customerAddress, a.DICT_MARRY_STATUS as dictMartyStatus, c.COMP_NAME as pCompName, d.MATE_TEL as mateTel, d.MATE_CERT_NUM as mateCertNum, d.MATE_NAME as mateName from t_jk_loan_customer a left join T_JK_LOAN_COMPANY b on a.loan_code = b.loan_code left join T_JK_LOAN_COMPANY c on a.loan_code = c.loan_code left join jk.T_JK_LOAN_MATE d on a.loan_code = d.loan_code where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.R_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and c.R_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and d.r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.DICT_R_CUSTOMTER_TYPE = '0' and c.DICT_R_CUSTOMTER_TYPE = '2' and d.LOAN_CUSTOMTER_TYPE = '0') loaninfo on report.loan_code = loaninfo.loan_code

