and det.loan_Code = #{loanCode,jdbcType=VARCHAR}
and det.id = #{id,jdbcType=VARCHAR}
order by occ.GETINFO_TIME desc limit 1 ) b on a.id=b.id left join ( select det.id, concat(liv.LIVE_PROVINCE,liv.LIVE_CITY,liv.LIVE_AREA,liv.LIVE_ADDRESS) as liveAddress from jk.t_jk_credit_report_detailed det left join jk.T_JK_CREDIT_LIVE_INFO liv on det.id = liv.RELATION_ID where 1=1
and det.loan_Code = #{loanCode,jdbcType=VARCHAR}
and det.id = #{id,jdbcType=VARCHAR}
order by liv.GETINFO_TIME desc limit 1 )c on a.id=c.id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.id = #{id,jdbcType=VARCHAR}
) report left join (select a.loan_code, a.COBO_CERT_NUM as customerCertNum, a.COBO_MOBILE as customerPhoneFirst, a.COBO_MOBILE2 as customerPhoneSecond, b.COMP_NAME as compName, a.COBO_FAMILY_TEL as customerTel, b.COMP_TEL as compTel, concat( b.COMP_PROVINCE,b.COMP_CITY,b.COMP_ARER,b.COMP_ADDRESS ) as compAddress, concat( a.COBO_LIVEING_PROVINCE,a.COBO_LIVEING_CITY,a.COBO_LIVEING_AREA,a.COBO_NOW_ADDRESS ) as customerAddress, a.DICT_MARRY_STATUS as dictMartyStatus, c.COMP_NAME as pCompName, d.MATE_TEL as mateTel, d.MATE_CERT_NUM as mateCertNum, d.MATE_NAME as mateName from jk.T_JK_LOAN_COBORROWER a left join jk.T_JK_LOAN_COMPANY b on a.loan_code = b.loan_code left join jk.T_JK_LOAN_COMPANY c on a.loan_code = c.loan_code left join jk.T_JK_LOAN_MATE d on a.loan_code = d.loan_code where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.R_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and c.R_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and d.r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
and b.DICT_R_CUSTOMTER_TYPE = '0' and c.DICT_R_CUSTOMTER_TYPE = '2' and d.LOAN_CUSTOMTER_TYPE = '0') loaninfo on report.loan_code = loaninfo.loan_code

