select max(c.current_overdue) as currentOverdue from jk.t_jk_credit_report_detailed a left join jk.t_jk_credit_loan_detailed_one b on a.id = b.relation_id left join jk.t_jk_credit_loan_detailed_two c on b.id = c.relation_id where 1=1
