mapper.select:
select SUM(b."LIMIT") as limit, SUM(b.used_limit) as usedLimit, SUM(b.overdue_amount) as overdueAmount from jk.t_jk_credit_report_simple a left join jk.t_jk_credit_card_info b on a.id = b.relation_id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}
and a.r_customer_coborrower_id = #{rId,jdbcType=VARCHAR}

select SUM(case when (b.abort_day - b.issue_day)/30 > 0 and b.CONTEACT_AMOUNT > 0 then b.CONTEACT_AMOUNT/((b.abort_day - b.issue_day)/30) else 0 end) as overdueAmount, sum(b.CONTEACT_AMOUNT) as conteactAmount, sum(b.LOAN_BALANCE) as loanBalance from jk.t_jk_credit_report_simple a left join jk.t_jk_credit_loan_info b on a.id = b.relation_id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}
a.r_customer_coborrower_id = #{rId,jdbcType=VARCHAR}
and b.currency in
#{item}

select
	max(overdueAmount) as overdueNo
from(
	(select 
		max(b.OVERDUE_AMOUNT) as overdueAmount
	from 
		jk.t_jk_credit_report_simple a
	left join 
		jk.t_jk_credit_card_info b
	on a.id = b.relation_id
	where 
		1=1

			and a.loan_Code = #{loanCode,jdbcType=VARCHAR}


			and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}


			and a.R_CUSTOMER_COBORROWER_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}

		)
	union all
	(select 
		max(b.OVERDUE_AMOUNT) as overdueAmount
	from 
		jk.t_jk_credit_report_simple a
	left join 
		jk.t_jk_credit_loan_info b
	on a.id = b.relation_id
	where 
		1=1

			and a.loan_Code = #{loanCode,jdbcType=VARCHAR}


			and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}


			and a.R_CUSTOMER_COBORROWER_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}

		)
	)

select count(*) as overdueNo from jk.t_jk_credit_report_simple a left join jk.t_jk_credit_loan_info b on a.id = b.relation_id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}
and a.R_CUSTOMER_COBORROWER_ID = #{rId,jdbcType=VARCHAR}
and b.ACCOUNT_STATUS = #{accountStatus,jdbcType=VARCHAR}
and b.ACTUAL_DAY < now()

select count(*) as overdueNo from jk.t_jk_credit_report_simple a left join jk.t_jk_credit_card_info b on a.id = b.relation_id left join jk.T_JK_LOAN_INFO c on a.loan_code = c.loan_code where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}
and a.R_CUSTOMER_COBORROWER_ID = #{rId,jdbcType=VARCHAR}
and c.LOAN_AUDIT_PRODUCT in
#{item}

select count(b.id) as num from jk.t_jk_credit_report_simple a left join jk.T_JK_CREDIT_GUARANTEE_DETAILED b on a.id = b.RELATION_ID where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}
and a.R_CUSTOMER_COBORROWER_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}

select count(*) from jk.t_jk_credit_report_simple a left join jk.T_JK_CREDIT_QUERY_RECORD b on a.id = b.RELATION_ID where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}
and a.R_CUSTOMER_COBORROWER_ID = #{rId,jdbcType=VARCHAR}
and b.QUERY_TYPE = #{accountStatus,jdbcType=VARCHAR}
and b.QUERY_DAY > (now()-interval '6 month')

select b."LIMIT", b.USED_LIMIT from jk.t_jk_credit_report_simple a left join jk.T_JK_CREDIT_CARD_INFO b on a.id = b.relation_id where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}
and a.R_CUSTOMER_COBORROWER_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}

select c.label as CURRENCY, b.CONTEACT_AMOUNT, (b.ISSUE_DAY-b.ACTUAL_DAY)/30 as OVERDUE_NO, b.LOAN_BALANCE, b.OVERDUE_AMOUNT from jk.t_jk_credit_report_simple a left join jk.t_jk_credit_loan_info b on a.id = b.relation_id left join jk.t_gl_dict c on b.CURRENCY = c.value and c.type = 'jk_credit_loan_type_flag' where 1=1
and a.loan_Code = #{loanCode,jdbcType=VARCHAR}
and a.DICT_CUSTOMER_TYPE = #{dictCustomerType,jdbcType=VARCHAR}
and a.R_CUSTOMER_COBORROWER_ID = #{rCustomerCoborrowerId,jdbcType=VARCHAR}

select 

from jk.t_jk_credit_report_simple 
where
	1=1

		and id = #{id,jdbcType=VARCHAR}


    	and loan_code = #{loanCode,jdbcType=VARCHAR}


    	and dict_customer_type = #{dictCustomerType,jdbcType=VARCHAR}


    	and r_customer_coborrower_id = #{rCustomerCoborrowerId,jdbcType=VARCHAR}
